namespace Mageplaza\SeoPro\Setup;

use Magento\Framework\DB\Ddl\Table;
use Magento\Framework\Setup\UpgradeSchemaInterface;
use Magento\Framework\Setup\SchemaSetupInterface;
use Magento\Framework\Setup\ModuleContextInterface;

/**
 * Class UpgradeSchema
 * @package Mageplaza\SeoPro\Setup
 */
class UpgradeSchema implements UpgradeSchemaInterface
{
    /**
     * @param SchemaSetupInterface $setup
     * @param ModuleContextInterface $context
     */
    public function upgrade(
        SchemaSetupInterface $setup,
        ModuleContextInterface $context
    ) {
        $installer = $setup;

        $installer->startSetup();

        if (version_compare($context->getVersion(), '2.0.0', '<')) {
            $tableName = $setup->getTable('cms_page');
            if (!$setup->getConnection()->tableColumnExists($tableName, 'mp_canonical_url')) {
                $setup->getConnection()->addColumn($setup->getTable('cms_page'), 'mp_canonical_url', [
                    'type'     => Table::TYPE_TEXT,
                    'nullable' => true,
                    'comment'  => 'Canonical URL',
                ]);
            }
        }

        $installer->endSetup();
    }
}
